const Todo = props => {
  return (
    <div>
      {todos.map((todo, i) => {
        return (
          <div key={i}>
            <input
              onChange={e => {
                handletoggleComplete(i)
              }}
              checked={todo.completed}
              type='checkbox'
            />
            <span className={todo.completed ? 'line-through' : null}>
              {todo.text}
            </span>
            <BsTrashFill size={20} onClick={e => handleTododelete(i)} />
          </div>
        )
      })}
    </div>
  )
}

export default Todo
