import { link } from 'react-router-dom'

import { AiOutlineHome } from 'react-icons/ai'
import { BsCheckSquare, BsListTask } from 'react-icons/bs'
import { IoIosClose } from 'react-icons/io'
import '../style/sidebar.css'

const Sidebar = props => {
  const { sidebarVisible, setSidebarVisible } = props
  const handleSidebar = () => {
    setSidebarVisible(prevState => !prevState)
  }
  return (
    <div
      className={
        sidebarVisible ? 'sidebar activeSidebar' : 'sidebar InactiveSidebar'
      }
    >
      <IoIosClose size={50} onclick={handleSidebar} />
      <div>
        <h1 classNme='card-heade'> TODO APP </h1>
      </div>
    </div>
  )
}

export default Sidebar
